package com.example.songr.controllers;

import com.example.songr.models.Album;
import com.example.songr.repositories.AlbumRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.ModelAttribute;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.servlet.view.RedirectView;

import java.util.ArrayList;
import java.util.List;

@Controller
public class AlbumsController {

    @Autowired
    AlbumRepository albumRepository;

    @GetMapping("/albums")
    public String getAlbums(Model model){
        Album album1 = new Album("What's Going On", "Marvin Gaye's",10, (int) 350, "data:image/jpeg;base64,");
        Album album2 = new Album("Blue", "Joni Mitchell", 4, (int) 407, "data:image/jpeg;base64,");
        Album album3 = new Album("Purple Rain","Prince and the Revolution", 7, (int) 550,"data:image/jpeg;base64,");
        List<Album> albums = albumRepository.findAll();
        albums.add(album1);
        albums.add(album2);
        albums.add(album3);
        model.addAttribute("albums",albums);
        return "albums";
    }

    @PostMapping("/addAlbum")
    public RedirectView addAlbum(@ModelAttribute Album newAlbum) {
        albumRepository.save(newAlbum);
        return new RedirectView("/albums");
    }
}




